import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    console.log(`Setting up strategy for domain: ${domain}`);
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
    console.log(`Strategy registered: replitauth:${domain}`);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    console.log(`Login request for hostname: ${req.hostname}`);
    console.log(`Available strategies:`, passport._strategies);

    // Try to authenticate with the strategy for this hostname
    const strategyName = `replitauth:${req.hostname}`;
    console.log(`Attempting to use strategy: ${strategyName}`);

    if (!passport._strategy(strategyName)) {
      console.error(`Strategy ${strategyName} not found`);
      return res.status(500).json({ error: `Authentication strategy not configured for ${req.hostname}` });
    }

    passport.authenticate(strategyName, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    console.log(`Callback request for hostname: ${req.hostname}`);
    passport.authenticate(`replitauth:${req.hostname}`, {
      successRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer, WebSocket } from "ws";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { insertLeadSchema, insertFreelancerProfileSchema, insertSubscriptionSchema, insertLeadInterestSchema } from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // WebSocket server for real-time notifications
  const httpServer = createServer(app);
  const wss = new WebSocketServer({ server: httpServer, path: '/ws' });

  const connectedClients = new Map<string, WebSocket>();

  wss.on('connection', (ws, req) => {
    // Extract user ID from query params or handle auth here
    const userId = new URL(req.url!, `http://${req.headers.host}`).searchParams.get('userId');

    if (userId) {
      connectedClients.set(userId, ws);

      ws.on('close', () => {
        connectedClients.delete(userId);
      });
    }
  });

  // Broadcast notification to specific user
  const notifyUser = (userId: string, data: any) => {
    const client = connectedClients.get(userId);
    if (client && client.readyState === WebSocket.OPEN) {
      client.send(JSON.stringify(data));
    }
  };

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Role selection
  app.post('/api/auth/select-role', isAuthenticated, async (req: any, res) => {
    try {
      const { role } = req.body;
      const userId = req.user.claims.sub;

      if (!['customer', 'freelancer', 'admin'].includes(role)) {
        return res.status(400).json({ message: "Invalid role" });
      }

      await storage.updateUserRole(userId, role);
      res.json({ success: true });
    } catch (error) {
      console.error("Error updating role:", error);
      res.status(500).json({ message: "Failed to update role" });
    }
  });

  // Categories
  app.get('/api/categories', async (req, res) => {
    try {
      const categories = await storage.getCategories();
      res.json(categories);
    } catch (error) {
      console.error("Error fetching categories:", error);
      res.status(500).json({ message: "Failed to fetch categories" });
    }
  });

  // Freelancer profile routes
  app.get('/api/freelancer/profile', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const profile = await storage.getFreelancerProfile(userId);
      res.json(profile);
    } catch (error) {
      console.error("Error fetching freelancer profile:", error);
      res.status(500).json({ message: "Failed to fetch profile" });
    }
  });

  app.post('/api/freelancer/profile', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const profileData = insertFreelancerProfileSchema.parse({
        ...req.body,
        userId
      });

      const profile = await storage.createFreelancerProfile(profileData);
      res.json(profile);
    } catch (error) {
      console.error("Error creating freelancer profile:", error);
      res.status(500).json({ message: "Failed to create profile" });
    }
  });

  app.get('/api/freelancer/leads/available', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const profile = await storage.getFreelancerProfile(userId);

      if (!profile) {
        return res.status(404).json({ message: "Freelancer profile not found" });
      }

      const leads = await storage.getAvailableLeads(profile.id);
      res.json(leads);
    } catch (error) {
      console.error("Error fetching available leads:", error);
      res.status(500).json({ message: "Failed to fetch leads" });
    }
  });

  app.get('/api/freelancer/leads/accepted', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const profile = await storage.getFreelancerProfile(userId);

      if (!profile) {
        return res.status(404).json({ message: "Freelancer profile not found" });
      }

      const leads = await storage.getLeadsByFreelancer(profile.id);
      res.json(leads);
    } catch (error) {
      console.error("Error fetching accepted leads:", error);
      res.status(500).json({ message: "Failed to fetch leads" });
    }
  });

  app.post('/api/freelancer/leads/:leadId/accept', isAuthenticated, async (req: any, res) => {
    try {
      const { leadId } = req.params;
      const userId = req.user.claims.sub;
      const profile = await storage.getFreelancerProfile(userId);

      if (!profile) {
        return res.status(404).json({ message: "Freelancer profile not found" });
      }

      // Check if freelancer has active lead plan
      const hasLeadPlan = await storage.hasActiveLeadPlan(profile.id);
      if (!hasLeadPlan) {
        return res.status(403).json({ message: "Active lead plan required to accept leads" });
      }

      await storage.acceptLead(leadId, profile.id);

      // Get lead details to notify customer
      const lead = await storage.getLeadById(leadId);
      if (lead) {
        notifyUser(lead.customerId, {
          type: 'lead_accepted',
          leadId,
          freelancer: profile
        });
      }

      res.json({ success: true });
    } catch (error) {
      console.error("Error accepting lead:", error);
      res.status(500).json({ message: "Failed to accept lead" });
    }
  });

  app.post('/api/freelancer/leads/:leadId/interest', isAuthenticated, async (req: any, res) => {
    try {
      const { leadId } = req.params;
      const userId = req.user.claims.sub;
      const profile = await storage.getFreelancerProfile(userId);

      if (!profile) {
        return res.status(404).json({ message: "Freelancer profile not found" });
      }

      const interest = await storage.expressInterest({
        leadId,
        freelancerId: profile.id
      });

      res.json(interest);
    } catch (error) {
      console.error("Error expressing interest:", error);
      res.status(500).json({ message: "Failed to express interest" });
    }
  });

  // Customer routes
  app.get('/api/customer/leads', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const leads = await storage.getLeadsByCustomer(userId);
      res.json(leads);
    } catch (error) {
      console.error("Error fetching customer leads:", error);
      res.status(500).json({ message: "Failed to fetch leads" });
    }
  });

  app.post('/api/customer/leads', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const leadData = insertLeadSchema.parse({
        ...req.body,
        customerId: userId
      });

      const lead = await storage.createLead(leadData);

      // Notify relevant freelancers
      const freelancers = await storage.getFreelancersByCategory(leadData.categoryId, leadData.pincode || '');
      for (const freelancer of freelancers) {
        notifyUser(freelancer.userId, {
          type: 'new_lead',
          lead: { ...lead, category: freelancer.category }
        });
      }

      res.json(lead);
    } catch (error) {
      console.error("Error creating lead:", error);
      res.status(500).json({ message: "Failed to create lead" });
    }
  });

  app.get('/api/customer/freelancers', async (req, res) => {
    try {
      const { categoryId, area } = req.query;

      if (!categoryId) {
        return res.status(400).json({ message: "Category ID is required" });
      }

      // Get freelancers with position plans first, then regular freelancers
      const positionFreelancers = area ? 
        await storage.getPositionPlanFreelancers(categoryId as string, area as string) : [];
      const regularFreelancers = await storage.getFreelancersByCategory(categoryId as string, area as string);

      // Remove duplicates and maintain position order
      const freelancerMap = new Map();

      // Add position plan freelancers first
      positionFreelancers.forEach(f => freelancerMap.set(f.id, f));

      // Add regular freelancers
      regularFreelancers.forEach(f => {
        if (!freelancerMap.has(f.id)) {
          freelancerMap.set(f.id, f);
        }
      });

      const allFreelancers = Array.from(freelancerMap.values());
      res.json(allFreelancers);
    } catch (error) {
      console.error("Error fetching freelancers:", error);
      res.status(500).json({ message: "Failed to fetch freelancers" });
    }
  });

  // Subscription routes
  app.get('/api/freelancer/subscriptions', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const profile = await storage.getFreelancerProfile(userId);

      if (!profile) {
        return res.status(404).json({ message: "Freelancer profile not found" });
      }

      const subscriptions = await storage.getActiveSubscriptions(profile.id);
      res.json(subscriptions);
    } catch (error) {
      console.error("Error fetching subscriptions:", error);
      res.status(500).json({ message: "Failed to fetch subscriptions" });
    }
  });

  app.post('/api/freelancer/subscriptions', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const profile = await storage.getFreelancerProfile(userId);

      if (!profile) {
        return res.status(404).json({ message: "Freelancer profile not found" });
      }

      const subscriptionData = insertSubscriptionSchema.parse({
        ...req.body,
        freelancerId: profile.id
      });

      const subscription = await storage.createSubscription(subscriptionData);
      res.json(subscription);
    } catch (error) {
      console.error("Error creating subscription:", error);
      res.status(500).json({ message: "Failed to create subscription" });
    }
  });

  // Admin routes
  app.get('/api/admin/users', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (user?.role !== 'admin') {
        return res.status(403).json({ message: "Admin access required" });
      }

      const users = await storage.getAllUsers();
      res.json(users);
    } catch (error) {
      console.error("Error fetching users:", error);
      res.status(500).json({ message: "Failed to fetch users" });
    }
  });

  app.get('/api/admin/leads', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (user?.role !== 'admin') {
        return res.status(403).json({ message: "Admin access required" });
      }

      const leads = await storage.getAllLeads();
      res.json(leads);
    } catch (error) {
      console.error("Error fetching leads:", error);
      res.status(500).json({ message: "Failed to fetch leads" });
    }
  });

  app.get('/api/admin/verifications/pending', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (user?.role !== 'admin') {
        return res.status(403).json({ message: "Admin access required" });
      }

      const pendingVerifications = await storage.getPendingVerifications();
      res.json(pendingVerifications);
    } catch (error) {
      console.error("Error fetching pending verifications:", error);
      res.status(500).json({ message: "Failed to fetch verifications" });
    }
  });

  app.post('/api/admin/verifications/:freelancerId/:status', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (user?.role !== 'admin') {
        return res.status(403).json({ message: "Admin access required" });
      }

      const { freelancerId, status } = req.params;

      if (!['approved', 'rejected'].includes(status)) {
        return res.status(400).json({ message: "Invalid status" });
      }

      await storage.updateVerificationStatus(freelancerId, status as 'approved' | 'rejected');
      res.json({ success: true });
    } catch (error) {
      console.error("Error updating verification status:", error);
      res.status(500).json({ message: "Failed to update verification" });
    }
  });

  return httpServer;
}

server/storage.ts.content:
import {
  users,
  categories,
  freelancerProfiles,
  leads,
  subscriptions,
  leadInterests,
  type User,
  type UpsertUser,
  type Category,
  type InsertCategory,
  type FreelancerProfile,
  type InsertFreelancerProfile,
  type Lead,
  type InsertLead,
  type Subscription,
  type InsertSubscription,
  type LeadInterest,
  type InsertLeadInterest,
  type LeadWithRelations,
  type FreelancerWithRelations,
} from "@shared/schema";
import { db } from "./db";
import { eq, and, desc, asc, inArray, sql } from "drizzle-orm";

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;
  updateUserRole(id: string, role: 'customer' | 'freelancer' | 'admin'): Promise<void>;

  // Category operations
  getCategories(): Promise<Category[]>;
  createCategory(category: InsertCategory): Promise<Category>;

  // Freelancer operations
  getFreelancerProfile(userId: string): Promise<FreelancerProfile | undefined>;
  createFreelancerProfile(profile: InsertFreelancerProfile): Promise<FreelancerProfile>;
  updateFreelancerProfile(id: string, updates: Partial<FreelancerProfile>): Promise<void>;
  getFreelancersByCategory(categoryId: string, area?: string): Promise<FreelancerWithRelations[]>;
  updateFreelancerOnlineStatus(freelancerId: string, isOnline: boolean): Promise<void>;

  // Lead operations
  createLead(lead: InsertLead): Promise<Lead>;
  getLeadsByFreelancer(freelancerId: string): Promise<LeadWithRelations[]>;
  getLeadsByCustomer(customerId: string): Promise<LeadWithRelations[]>;
  getAvailableLeads(freelancerId: string): Promise<LeadWithRelations[]>;
  acceptLead(leadId: string, freelancerId: string): Promise<void>;
  getLeadById(id: string): Promise<LeadWithRelations | undefined>;

  // Subscription operations
  createSubscription(subscription: InsertSubscription): Promise<Subscription>;
  getActiveSubscriptions(freelancerId: string): Promise<Subscription[]>;
  hasActiveLeadPlan(freelancerId: string): Promise<boolean>;
  getPositionPlanFreelancers(categoryId: string, area: string): Promise<FreelancerWithRelations[]>;

  // Lead interest operations
  expressInterest(leadInterest: InsertLeadInterest): Promise<LeadInterest>;
  getLeadInterests(leadId: string): Promise<LeadInterest[]>;

  // Admin operations
  getPendingVerifications(): Promise<FreelancerWithRelations[]>;
  updateVerificationStatus(freelancerId: string, status: 'approved' | 'rejected'): Promise<void>;
  getAllUsers(): Promise<User[]>;
  getAllLeads(): Promise<LeadWithRelations[]>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  async updateUserRole(id: string, role: 'customer' | 'freelancer' | 'admin'): Promise<void> {
    await db.update(users).set({ role, updatedAt: new Date() }).where(eq(users.id, id));
  }

  // Category operations
  async getCategories(): Promise<Category[]> {
    return await db.select().from(categories).where(eq(categories.isActive, true)).orderBy(asc(categories.name));
  }

  async createCategory(category: InsertCategory): Promise<Category> {
    const [newCategory] = await db.insert(categories).values(category).returning();
    return newCategory;
  }

  // Freelancer operations
  async getFreelancerProfile(userId: string): Promise<FreelancerProfile | undefined> {
    const [profile] = await db.select().from(freelancerProfiles).where(eq(freelancerProfiles.userId, userId));
    return profile;
  }

  async createFreelancerProfile(profile: InsertFreelancerProfile): Promise<FreelancerProfile> {
    const [newProfile] = await db.insert(freelancerProfiles).values(profile).returning();
    return newProfile;
  }

  async updateFreelancerProfile(id: string, updates: Partial<FreelancerProfile>): Promise<void> {
    await db.update(freelancerProfiles).set({ ...updates, updatedAt: new Date() }).where(eq(freelancerProfiles.id, id));
  }

  async getFreelancersByCategory(categoryId: string, area?: string): Promise<FreelancerWithRelations[]> {
    const query = db
      .select()
      .from(freelancerProfiles)
      .leftJoin(users, eq(freelancerProfiles.userId, users.id))
      .leftJoin(categories, eq(freelancerProfiles.categoryId, categories.id))
      .leftJoin(subscriptions, eq(freelancerProfiles.id, subscriptions.freelancerId))
      .where(
        and(
          eq(freelancerProfiles.categoryId, categoryId),
          eq(freelancerProfiles.verificationStatus, 'approved'),
          area ? sql`${area} = ANY(${freelancerProfiles.workingAreas})` : undefined
        )
      )
      .orderBy(desc(freelancerProfiles.rating));

    const results = await query;

    // Transform results to match FreelancerWithRelations type
    const freelancersMap = new Map<string, FreelancerWithRelations>();

    for (const row of results) {
      const freelancerId = row.freelancer_profiles.id;
      if (!freelancersMap.has(freelancerId)) {
        freelancersMap.set(freelancerId, {
          ...row.freelancer_profiles,
          user: row.users!,
          category: row.categories!,
          subscriptions: []
        });
      }

      if (row.subscriptions) {
        freelancersMap.get(freelancerId)!.subscriptions.push(row.subscriptions);
      }
    }

    return Array.from(freelancersMap.values());
  }

  async updateFreelancerOnlineStatus(freelancerId: string, isOnline: boolean): Promise<void> {
    await db.update(freelancerProfiles).set({ 
      isOnline, 
      lastSeen: new Date(),
      updatedAt: new Date() 
    }).where(eq(freelancerProfiles.id, freelancerId));
  }

  // Lead operations
  async createLead(lead: InsertLead): Promise<Lead> {
    const [newLead] = await db.insert(leads).values(lead).returning();
    return newLead;
  }

  async getLeadsByFreelancer(freelancerId: string): Promise<LeadWithRelations[]> {
    const results = await db
      .select()
      .from(leads)
      .leftJoin(users, eq(leads.customerId, users.id))
      .leftJoin(categories, eq(leads.categoryId, categories.id))
      .leftJoin(freelancerProfiles, eq(leads.acceptedBy, freelancerProfiles.id))
      .where(eq(leads.acceptedBy, freelancerId))
      .orderBy(desc(leads.createdAt));

    return results.map(row => ({
      ...row.leads,
      customer: row.users!,
      category: row.categories!,
      acceptedByFreelancer: row.freelancer_profiles ? {
        ...row.freelancer_profiles,
        user: row.users!,
        category: row.categories!,
        subscriptions: []
      } : undefined
    }));
  }

  async getLeadsByCustomer(customerId: string): Promise<LeadWithRelations[]> {
    const results = await db
      .select()
      .from(leads)
      .leftJoin(users, eq(leads.customerId, users.id))
      .leftJoin(categories, eq(leads.categoryId, categories.id))
      .leftJoin(freelancerProfiles, eq(leads.acceptedBy, freelancerProfiles.id))
      .where(eq(leads.customerId, customerId))
      .orderBy(desc(leads.createdAt));

    return results.map(row => ({
      ...row.leads,
      customer: row.users!,
      category: row.categories!,
      acceptedByFreelancer: row.freelancer_profiles ? {
        ...row.freelancer_profiles,
        user: row.users!,
        category: row.categories!,
        subscriptions: []
      } : undefined
    }));
  }

  async getAvailableLeads(freelancerId: string): Promise<LeadWithRelations[]> {
    // Get freelancer's profile to determine category and working areas
    const [freelancer] = await db
      .select()
      .from(freelancerProfiles)
      .where(eq(freelancerProfiles.id, freelancerId));

    if (!freelancer) return [];

    const results = await db
      .select()
      .from(leads)
      .leftJoin(users, eq(leads.customerId, users.id))
      .leftJoin(categories, eq(leads.categoryId, categories.id))
      .where(
        and(
          eq(leads.status, 'pending'),
          eq(leads.categoryId, freelancer.categoryId),
          freelancer.workingAreas ? sql`${leads.pincode} = ANY(${freelancer.workingAreas}) OR ${leads.location} = ANY(${freelancer.workingAreas})` : undefined
        )
      )
      .orderBy(desc(leads.createdAt));

    return results.map(row => ({
      ...row.leads,
      customer: row.users!,
      category: row.categories!
    }));
  }

  async acceptLead(leadId: string, freelancerId: string): Promise<void> {
    await db.update(leads).set({ 
      acceptedBy: freelancerId, 
      acceptedAt: new Date(),
      status: 'accepted',
      updatedAt: new Date()
    }).where(eq(leads.id, leadId));
  }

  async getLeadById(id: string): Promise<LeadWithRelations | undefined> {
    const [result] = await db
      .select()
      .from(leads)
      .leftJoin(users, eq(leads.customerId, users.id))
      .leftJoin(categories, eq(leads.categoryId, categories.id))
      .leftJoin(freelancerProfiles, eq(leads.acceptedBy, freelancerProfiles.id))
      .where(eq(leads.id, id));

    if (!result) return undefined;

    return {
      ...result.leads,
      customer: result.users!,
      category: result.categories!,
      acceptedByFreelancer: result.freelancer_profiles ? {
        ...result.freelancer_profiles,
        user: result.users!,
        subscriptions: []
      } : undefined
    };
  }

  // Subscription operations
  async createSubscription(subscription: InsertSubscription): Promise<Subscription> {
    const [newSubscription] = await db.insert(subscriptions).values(subscription).returning();
    return newSubscription;
  }

  async getActiveSubscriptions(freelancerId: string): Promise<Subscription[]> {
    return await db
      .select()
      .from(subscriptions)
      .where(
        and(
          eq(subscriptions.freelancerId, freelancerId),
          eq(subscriptions.status, 'active'),
          sql`${subscriptions.endDate} > NOW()`
        )
      );
  }

  async hasActiveLeadPlan(freelancerId: string): Promise<boolean> {
    const [result] = await db
      .select({ count: sql<number>`count(*)` })
      .from(subscriptions)
      .where(
        and(
          eq(subscriptions.freelancerId, freelancerId),
          eq(subscriptions.type, 'lead'),
          eq(subscriptions.status, 'active'),
          sql`${subscriptions.endDate} > NOW()`
        )
      );

    return (result?.count || 0) > 0;
  }

  async getPositionPlanFreelancers(categoryId: string, area: string): Promise<FreelancerWithRelations[]> {
    const results = await db
      .select()
      .from(freelancerProfiles)
      .leftJoin(users, eq(freelancerProfiles.userId, users.id))
      .leftJoin(categories, eq(freelancerProfiles.categoryId, categories.id))
      .leftJoin(subscriptions, and(
        eq(freelancerProfiles.id, subscriptions.freelancerId),
        eq(subscriptions.type, 'position'),
        eq(subscriptions.status, 'active'),
        eq(subscriptions.categoryId, categoryId),
        eq(subscriptions.area, area),
        sql`${subscriptions.endDate} > NOW()`
      ))
      .where(
        and(
          eq(freelancerProfiles.categoryId, categoryId),
          eq(freelancerProfiles.verificationStatus, 'approved'),
          sql`${area} = ANY(${freelancerProfiles.workingAreas})`
        )
      )
      .orderBy(asc(subscriptions.position));

    const freelancersMap = new Map<string, FreelancerWithRelations>();

    for (const row of results) {
      const freelancerId = row.freelancer_profiles.id;
      if (!freelancersMap.has(freelancerId)) {
        freelancersMap.set(freelancerId, {
          ...row.freelancer_profiles,
          user: row.users!,
          category: row.categories!,
          subscriptions: []
        });
      }

      if (row.subscriptions) {
        freelancersMap.get(freelancerId)!.subscriptions.push(row.subscriptions);
      }
    }

    return Array.from(freelancersMap.values());
  }

  // Lead interest operations
  async expressInterest(leadInterest: InsertLeadInterest): Promise<LeadInterest> {
    const [newInterest] = await db.insert(leadInterests).values(leadInterest).returning();
    return newInterest;
  }

  async getLeadInterests(leadId: string): Promise<LeadInterest[]> {
    return await db.select().from(leadInterests).where(eq(leadInterests.leadId, leadId));
  }

  // Admin operations
  async getPendingVerifications(): Promise<FreelancerWithRelations[]> {
    const results = await db
      .select()
      .from(freelancerProfiles)
      .leftJoin(users, eq(freelancerProfiles.userId, users.id))
      .leftJoin(categories, eq(freelancerProfiles.categoryId, categories.id))
      .where(eq(freelancerProfiles.verificationStatus, 'pending'))
      .orderBy(desc(freelancerProfiles.createdAt));

    return results.map(row => ({
      ...row.freelancer_profiles,
      user: row.users!,
      category: row.categories!,
      subscriptions: []
    }));
  }

  async updateVerificationStatus(freelancerId: string, status: 'approved' | 'rejected'): Promise<void> {
    await db.update(freelancerProfiles).set({ 
      verificationStatus: status,
      updatedAt: new Date()
    }).where(eq(freelancerProfiles.id, freelancerId));
  }

  async getAllUsers(): Promise<User[]> {
    return await db.select().from(users).orderBy(desc(users.createdAt));
  }

  async getAllLeads(): Promise<LeadWithRelations[]> {
    const results = await db
      .select()
      .from(leads)
      .leftJoin(users, eq(leads.customerId, users.id))
      .leftJoin(categories, eq(leads.categoryId, categories.id))
      .leftJoin(freelancerProfiles, eq(leads.acceptedBy, freelancerProfiles.id))
      .orderBy(desc(leads.createdAt));

    return results.map(row => ({
      ...row.leads,
      customer: row.users!,
      category: row.categories!,
      acceptedByFreelancer: row.freelancer_profiles ? {
        ...row.freelancer_profiles,
        user: row.users!,
        category: row.categories!,
        subscriptions: []
      } : undefined
    }));
  }
}

export const storage = new DatabaseStorage();

