@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  /* Dark purple theme */
  --background: hsl(252 47% 11%); /* #1E1B2E */
  --foreground: hsl(0 0% 100%); /* White text */
  --card: hsl(252 35% 15%); /* Slightly lighter purple for cards */
  --card-foreground: hsl(0 0% 95%);
  --popover: hsl(252 47% 11%);
  --popover-foreground: hsl(0 0% 100%);
  --primary: hsl(267 83% 53%); /* Purple accent */
  --primary-foreground: hsl(0 0% 100%);
  --primary-light: hsl(267 66% 73%); /* Light purple */
  --primary-lighter: hsl(267 65% 80%); /* Lighter purple */
  --primary-dark: hsl(267 83% 43%); /* Darker purple */
  --secondary: hsl(252 25% 25%); /* Medium purple-gray */
  --secondary-foreground: hsl(0 0% 100%);
  --muted: hsl(252 20% 20%); /* Muted purple */
  --muted-foreground: hsl(0 0% 70%); /* Light gray text */
  --accent: hsl(267 50% 25%); /* Purple accent background */
  --accent-foreground: hsl(0 0% 100%);
  --destructive: hsl(356 90% 54%);
  --destructive-foreground: hsl(0 0% 100%);
  --border: hsl(252 20% 25%); /* Purple border */
  --input: hsl(252 25% 18%); /* Dark input background */
  --ring: hsl(267 83% 53%);
  --success: hsl(142 76% 45%);
  --warning: hsl(38 92% 60%);
  --error: hsl(0 84% 65%);
  --surface: hsl(252 47% 11%); /* Main background */
  --on-surface: hsl(0 0% 100%); /* White text on surface */
  --chart-1: hsl(267 83% 53%);
  --chart-2: hsl(159 100% 45%);
  --chart-3: hsl(42 92% 60%);
  --chart-4: hsl(147 78% 50%);
  --chart-5: hsl(341 75% 60%);
  --sidebar: hsl(252 35% 15%);
  --sidebar-foreground: hsl(0 0% 95%);
  --sidebar-primary: hsl(267 83% 53%);
  --sidebar-primary-foreground: hsl(0 0% 100%);
  --sidebar-accent: hsl(267 50% 25%);
  --sidebar-accent-foreground: hsl(0 0% 100%);
  --sidebar-border: hsl(252 20% 25%);
  --sidebar-ring: hsl(267 83% 53%);
  --font-sans: 'Inter', sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --radius: 1.3rem;
}

/* Remove dark mode override since we're always dark now */

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-surface text-on-surface;
  }
}

/* Purple gradient utilities */
.bg-gradient-purple {
  background: linear-gradient(135deg, hsl(var(--primary)) 0%, hsl(var(--primary-light)) 100%);
}

.bg-gradient-purple-light {
  background: linear-gradient(135deg, hsl(var(--primary-light)) 0%, hsl(var(--primary-lighter)) 100%);
}

/* Mobile container for app-like experience */
.mobile-container {
  max-width: 28rem;
  margin: 0 auto;
  background: hsl(var(--background));
  box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.5);
  min-height: 100vh;
  position: relative;
}

/* Status bar styling */
.status-bar {
  background: linear-gradient(135deg, hsl(var(--primary)) 0%, hsl(var(--primary-light)) 100%);
  color: white;
  padding: 0.5rem 1rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-size: 0.875rem;
}

/* Custom scrollbar for mobile feel */
::-webkit-scrollbar {
  width: 0px;
  background: transparent;
}

/* Smooth focus transitions */
input:focus, 
select:focus, 
textarea:focus {
  transition: border-color 0.2s ease-in-out, box-shadow 0.2s ease-in-out;
}

/* Button hover effects */
.hover-lift:hover {
  transform: translateY(-2px);
  transition: transform 0.2s ease-in-out;
}

/* Card hover effects */
.card-hover:hover {
  box-shadow: 0 10px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
  transition: box-shadow 0.2s ease-in-out;
}

/* Animation for page transitions */
.page-transition {
  animation: slideInFromRight 0.3s ease-out;
}

@keyframes slideInFromRight {
  from {
    transform: translateX(100%);
    opacity: 0;
  }
  to {
    transform: translateX(0);
    opacity: 1;
  }
}

/* Floating action button */
.fab {
  position: fixed;
  bottom: 5rem;
  right: 1.5rem;
  width: 3.5rem;
  height: 3.5rem;
  background: linear-gradient(135deg, hsl(var(--primary)) 0%, hsl(var(--primary-light)) 100%);
  color: white;
  border-radius: 50%;
  box-shadow: 0 10px 25px -5px rgba(107, 70, 193, 0.4);
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.25rem;
  border: none;
  cursor: pointer;
  transition: all 0.2s ease-in-out;
  z-index: 1000;
}

.fab:hover {
  transform: translateY(-2px);
  box-shadow: 0 15px 35px -5px rgba(107, 70, 193, 0.5);
}

/* Bottom navigation */
.bottom-nav {
  position: fixed;
  bottom: 0;
  left: 0;
  right: 0;
  background: hsl(var(--card));
  border-top: 1px solid hsl(var(--border));
  z-index: 100;
  backdrop-filter: blur(10px);
}

.bottom-nav .nav-item {
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 0.5rem 0.75rem;
  color: hsl(var(--muted-foreground));
  text-decoration: none;
  transition: color 0.2s ease-in-out;
}

.bottom-nav .nav-item.active {
  color: hsl(var(--primary));
}

.bottom-nav .nav-item i {
  font-size: 1.125rem;
  margin-bottom: 0.25rem;
}

.bottom-nav .nav-item span {
  font-size: 0.75rem;
  font-weight: 500;
}

/* Lead card animations */
.lead-card {
  animation: fadeInUp 0.3s ease-out;
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* Notification styles */
.notification-dot {
  position: absolute;
  top: -2px;
  right: -2px;
  width: 6px;
  height: 6px;
  background: hsl(var(--error));
  border-radius: 50%;
  border: 2px solid white;
}

/* Badge styles */
.badge-verified {
  background: hsl(var(--success));
  color: white;
  font-size: 0.75rem;
  font-weight: 600;
  padding: 0.25rem 0.5rem;
  border-radius: 0.5rem;
}

.badge-trusted {
  background: hsl(var(--warning));
  color: white;
  font-size: 0.75rem;
  font-weight: 600;
  padding: 0.25rem 0.5rem;
  border-radius: 0.5rem;
}

/* Position indicator */
.position-indicator {
  position: absolute;
  top: -8px;
  left: -8px;
  width: 24px;
  height: 24px;
  background: hsl(var(--warning));
  color: white;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 0.75rem;
  font-weight: bold;
  border: 2px solid white;
}

/* Loading spinner */
.spinner {
  width: 20px;
  height: 20px;
  border: 2px solid hsl(var(--muted));
  border-top: 2px solid hsl(var(--primary));
  border-radius: 50%;
  animation: spin 1s linear infinite;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

