import { useEffect, useState } from "react";
import { useLocation } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { useFirebaseAuth } from "@/hooks/useFirebaseAuth";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import Navigation from "@/components/navigation";
import type { Category, FreelancerWithRelations } from "@shared/schema";

export default function CustomerSearch() {
  const { isAuthenticated, isLoading } = useFirebaseAuth();
  const [, setLocation] = useLocation();
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("");

  // Redirect to landing if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      setLocation('/');
      return;
    }
  }, [isAuthenticated, isLoading, setLocation]);

  // Fetch categories
  const { data: categories } = useQuery<Category[]>({
    queryKey: ['/api/categories'],
    retry: false,
  });

  // Mock freelancers data for now
  const freelancers: FreelancerWithRelations[] = [];

  const filteredFreelancers = freelancers.filter(freelancer => {
    const matchesSearch = !searchQuery || 
      freelancer.user.firstName?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      freelancer.user.lastName?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      freelancer.bio?.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesCategory = !selectedCategory || freelancer.categoryId === selectedCategory;

    return matchesSearch && matchesCategory;
  });

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="spinner"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen pb-20">
      {/* Status Bar */}
      <div className="status-bar">
        <span>9:41 AM</span>
        <div className="flex space-x-1">
          <i className="fas fa-signal"></i>
          <i className="fas fa-wifi"></i>
          <i className="fas fa-battery-three-quarters"></i>
        </div>
      </div>

      {/* Header */}
      <div className="bg-gradient-purple text-white p-4">
        <div className="flex items-center justify-between mb-4">
          <button 
            onClick={() => setLocation('/customer')}
            className="text-white"
            data-testid="button-back"
          >
            <i className="fas fa-arrow-left text-lg"></i>
          </button>
          <h1 className="text-lg font-semibold">Search Freelancers</h1>
          <div></div>
        </div>

        {/* Search Bar */}
        <div className="relative mb-4">
          <Input
            type="text"
            placeholder="Search freelancers..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="w-full bg-white bg-opacity-90 text-gray-800 px-4 py-3 pl-10 rounded-xl placeholder-gray-500 border-0"
            data-testid="input-search-freelancers"
          />
          <i className="fas fa-search absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-500"></i>
        </div>

        {/* Category Filter */}
        <div className="flex gap-2 overflow-x-auto">
          <Button
            onClick={() => setSelectedCategory("")}
            variant={selectedCategory === "" ? "secondary" : "ghost"}
            size="sm"
            className="whitespace-nowrap text-white border-white/20"
            data-testid="filter-all"
          >
            All Categories
          </Button>
          {categories?.map((category) => (
            <Button
              key={category.id}
              onClick={() => setSelectedCategory(category.id)}
              variant={selectedCategory === category.id ? "secondary" : "ghost"}
              size="sm"
              className="whitespace-nowrap text-white border-white/20"
              data-testid={`filter-${category.id}`}
            >
              {category.name}
            </Button>
          ))}
        </div>
      </div>

      {/* Freelancers List */}
      <div className="p-4">
        {filteredFreelancers.length === 0 ? (
          <div className="text-center py-12">
            <i className="fas fa-users text-4xl text-gray-300 mb-4"></i>
            <h3 className="text-lg font-semibold text-gray-600 mb-2">No Freelancers Found</h3>
            <p className="text-gray-500 text-sm">
              {searchQuery || selectedCategory 
                ? "Try adjusting your search criteria" 
                : "Freelancers will appear here once they join the platform"}
            </p>
          </div>
        ) : (
          <div className="space-y-4">
            {filteredFreelancers.map((freelancer) => (
              <div key={freelancer.id} className="bg-white rounded-xl p-4 shadow-sm border border-gray-100">
                <div className="flex items-start space-x-3">
                  <div className="w-12 h-12 bg-gradient-purple rounded-full flex items-center justify-center text-white font-semibold">
                    {freelancer.user.firstName?.[0] || freelancer.user.email?.[0] || '?'}
                  </div>
                  <div className="flex-1">
                    <div className="flex items-center justify-between mb-1">
                      <h4 className="font-semibold text-gray-900" data-testid={`text-freelancer-name-${freelancer.id}`}>
                        {freelancer.user.firstName} {freelancer.user.lastName}
                      </h4>
                      <div className="flex items-center space-x-1">
                        <i className="fas fa-star text-yellow-400 text-xs"></i>
                        <span className="text-xs text-gray-600">{freelancer.rating}</span>
                      </div>
                    </div>
                    <p className="text-sm text-gray-600 mb-2">{freelancer.category.name}</p>
                    {freelancer.bio && (
                      <p className="text-xs text-gray-500 mb-2">{freelancer.bio}</p>
                    )}
                    <div className="flex items-center justify-between">
                      <span className="text-xs text-gray-500">
                        {freelancer.totalJobs} jobs completed
                      </span>
                      <Button
                        size="sm"
                        className="bg-gradient-purple hover:opacity-90"
                        data-testid={`button-contact-${freelancer.id}`}
                      >
                        Contact
                      </Button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Bottom Navigation */}
      <Navigation currentPage="search" userRole="customer" />
    </div>
  );
}

client/src/pages/freelancer-dashboard.ts.content:
import { useEffect, useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { useFirebaseAuth } from "@/hooks/useFirebaseAuth";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import LeadCard from "@/components/lead-card";
import Navigation from "@/components/navigation";
import type { FreelancerProfile, LeadWithRelations, Subscription } from "@shared/schema";

export default function FreelancerDashboard() {
  const { user: firebaseUser, isAuthenticated, isLoading } = useFirebaseAuth();
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [newLeadsCount, setNewLeadsCount] = useState(0);

  // Redirect to landing if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      setLocation('/');
      return;
    }
  }, [isAuthenticated, isLoading, setLocation]);

  // Mock data for now since backend API is using different auth
  const profile: FreelancerProfile = {
    id: 'mock-freelancer-1',
    userId: firebaseUser?.uid || 'mock-user',
    categoryId: '1',
    workingAreas: ['Mumbai', 'Navi Mumbai'],
    bio: 'Experienced professional offering quality services',
    experience: '5 years',
    isAvailable: true,
    verificationStatus: 'approved',
    profileCompletionScore: 85,
    lastSeen: new Date(),
    createdAt: new Date(),
    updatedAt: new Date(),
  };

  const subscriptions: Subscription[] = [];
  const availableLeads: LeadWithRelations[] = [
    {
      id: 'lead-1',
      title: 'Home Electrical Repair',
      description: 'Need to fix electrical issues in kitchen',
      budgetMin: 2000,
      budgetMax: 5000,
      location: 'Bandra, Mumbai',
      pincode: '400050',
      categoryId: '1',
      customerId: 'customer-1',
      status: 'pending',
      preferredTime: 'Morning',
      photos: [],
      createdAt: new Date(),
      updatedAt: new Date(),
      customer: {
        id: 'customer-1',
        firstName: 'John',
        lastName: 'Doe',
        email: 'john@example.com',
        role: 'customer' as const,
        profileImageUrl: null,
        createdAt: new Date(),
        updatedAt: new Date(),
      },
      category: {
        id: '1',
        name: 'Electrical',
        color: '#FFA500',
        icon: 'fas fa-bolt',
        createdAt: new Date(),
        isActive: true,
      },
    },
  ];
  const acceptedLeads: LeadWithRelations[] = [];

  const profileLoading = false;
  const subscriptionsLoading = false;
  const availableLeadsLoading = false;
  const acceptedLeadsLoading = false;

  // Accept lead mutation
  const acceptLeadMutation = useMutation({
    mutationFn: async (leadId: string) => {
      await apiRequest('POST', `/api/freelancer/leads/${leadId}/accept`);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Lead accepted successfully!",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/freelancer/leads/available'] });
      queryClient.invalidateQueries({ queryKey: ['/api/freelancer/leads/accepted'] });
    },
    onError: (error: any) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }

      toast({
        title: "Error",
        description: error.message || "Failed to accept lead",
        variant: "destructive",
      });
    },
  });

  // Express interest mutation
  const expressInterestMutation = useMutation({
    mutationFn: async (leadId: string) => {
      await apiRequest('POST', `/api/freelancer/leads/${leadId}/interest`);
    },
    onSuccess: () => {
      toast({
        title: "Interest Expressed",
        description: "Your interest has been recorded!",
      });
    },
    onError: (error: any) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }

      toast({
        title: "Error",
        description: error.message || "Failed to express interest",
        variant: "destructive",
      });
    },
  });

  const handleAcceptLead = (leadId: string) => {
    acceptLeadMutation.mutate(leadId);
  };

  const handleExpressInterest = (leadId: string) => {
    expressInterestMutation.mutate(leadId);
  };

  const handleViewPlans = () => {
    setLocation('/plans');
  };

  // Check if user has active lead plan
  const hasActiveLeadPlan = subscriptions?.some(
    sub => sub.type === 'lead' && sub.status === 'active' && new Date(sub.endDate) > new Date()
  );

  // Reset new leads count when viewing available leads
  useEffect(() => {
    if (availableLeads) {
      setNewLeadsCount(0);
    }
  }, [availableLeads]);

  if (isLoading || profileLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="spinner"></div>
      </div>
    );
  }

  // If no profile exists, redirect to profile creation
  if (!profile) {
    setLocation('/freelancer/profile-setup');
    return null;
  }

  return (
    <div className="min-h-screen pb-20 bg-background text-foreground">
      {/* Status Bar */}
      <div className="status-bar">
        <span>9:41 AM</span>
        <div className="flex space-x-1">
          <i className="fas fa-signal"></i>
          <i className="fas fa-wifi"></i>
          <i className="fas fa-battery-three-quarters"></i>
        </div>
      </div>

      {/* Header */}
      <div className="bg-gradient-purple text-white p-6">
        <div className="flex items-center justify-between mb-6">
          <div>
            <h2 className="text-xl font-bold mb-1" data-testid="text-freelancer-greeting">
              Welcome, {firebaseUser?.displayName || firebaseUser?.email?.split('@')[0] || 'Freelancer'}!
            </h2>
            <div className="flex items-center space-x-3">
              <span className="text-purple-100">Professional Freelancer</span>
              <div className="flex items-center space-x-2">
                <span className="bg-green-400 w-2 h-2 rounded-full"></span>
                <span className="text-xs text-purple-200">Available</span>
              </div>
            </div>
          </div>
          <div className="flex items-center space-x-3">
            {profile.verificationStatus === 'approved' && (
              <Badge className="bg-green-500/20 text-green-300 border-green-500/30">
                <i className="fas fa-check-circle mr-1"></i>
                VERIFIED
              </Badge>
            )}
            <div className="relative">
              <i className="fas fa-bell text-xl opacity-80"></i>
              {newLeadsCount > 0 && (
                <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center" data-testid="notification-dot">
                  {newLeadsCount}
                </span>
              )}
            </div>
          </div>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-3 gap-3">
          <div className="bg-white bg-opacity-20 rounded-xl p-3 text-center">
            <p className="text-2xl font-bold" data-testid="text-new-leads-count">
              {availableLeads?.length || 0}
            </p>
            <p className="text-xs text-purple-100">New Leads</p>
          </div>
          <div className="bg-white bg-opacity-20 rounded-xl p-3 text-center">
            <p className="text-2xl font-bold">â‚¹0</p>
            <p className="text-xs text-purple-100">This Month</p>
          </div>
          <div className="bg-white bg-opacity-20 rounded-xl p-3 text-center">
            <p className="text-2xl font-bold">{profile.rating}</p>
            <p className="text-xs text-purple-100">Rating</p>
          </div>
        </div>
      </div>

      {/* Subscription Status */}
      <div className="p-4">
        {hasActiveLeadPlan ? (
          <div className="bg-gradient-to-r from-green-500 to-green-600 text-white rounded-xl p-4 mb-4">
            <div className="flex items-center justify-between">
              <div>
                <h3 className="font-semibold">Lead Plan Active</h3>
                <p className="text-sm text-green-100">
                  Expires on {new Date(
                    subscriptions?.find(s => s.type === 'lead')?.endDate || ''
                  ).toLocaleDateString()}
                </p>
              </div>
              <i className="fas fa-crown text-xl"></i>
            </div>
          </div>
        ) : (
          <Card className="bg-yellow-50 border-yellow-200 mb-4">
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <h3 className="font-semibold text-yellow-800">Upgrade to Lead Plan</h3>
                  <p className="text-sm text-yellow-600">Accept leads and get customer details</p>
                </div>
                <Button 
                  onClick={handleViewPlans}
                  size="sm"
                  className="bg-yellow-500 hover:bg-yellow-600"
                  data-testid="button-view-plans"
                >
                  View Plans
                </Button>
              </div>
            </CardContent>
          </Card>
        )}

        {/* New Leads */}
        <div className="mb-6">
          <div className="flex items-center justify-between mb-4">
            <h3 className="font-semibold text-on-surface">New Leads</h3>
            <Button variant="ghost" size="sm" className="text-primary text-sm font-medium">
              View All
            </Button>
          </div>

          {availableLeadsLoading ? (
            <div className="flex justify-center py-8">
              <div className="spinner"></div>
            </div>
          ) : (
            <div className="space-y-3">
              {availableLeads && availableLeads.length > 0 ? (
                availableLeads.slice(0, 3).map((lead) => (
                  <LeadCard
                    key={lead.id}
                    lead={lead}
                    canAccept={hasActiveLeadPlan}
                    onAccept={() => handleAcceptLead(lead.id)}
                    onExpressInterest={() => handleExpressInterest(lead.id)}
                    isAccepting={acceptLeadMutation.isPending}
                    isExpressingInterest={expressInterestMutation.isPending}
                  />
                ))
              ) : (
                <div className="text-center py-8 text-gray-500">
                  <i className="fas fa-inbox text-4xl mb-4"></i>
                  <p>No new leads available</p>
                </div>
              )}
            </div>
          )}
        </div>

        {/* Quick Actions */}
        <div className="grid grid-cols-2 gap-3">
          <Button
            variant="outline"
            className="bg-white border border-gray-200 rounded-xl p-4 text-center hover:shadow-md transition-shadow h-auto flex-col"
            data-testid="button-analytics"
          >
            <i className="fas fa-chart-line text-primary text-xl mb-2"></i>
            <p className="text-sm font-medium text-gray-700">Analytics</p>
          </Button>
          <Button
            variant="outline"
            className="bg-white border border-gray-200 rounded-xl p-4 text-center hover:shadow-md transition-shadow h-auto flex-col"
            data-testid="button-export-data"
          >
            <i className="fas fa-download text-primary text-xl mb-2"></i>
            <p className="text-sm font-medium text-gray-700">Export Data</p>
          </Button>
        </div>
      </div>

      {/* Bottom Navigation */}
      <Navigation currentPage="dashboard" userRole="freelancer" />
    </div>
  );
}

